import React, { useContext, useEffect, useRef,useState } from "react";
import noteContext from "../context/notes/notesContext";
import AddNote from "./AddNote";
import NoteItem from "./NoteItem";
const Notes = () => {
  const context = useContext(noteContext);
  const { notes, getNotes } = context;
  useEffect(() => {
    getNotes();
  }, []);
  const [note, setnote] = useState({title:"",description:"",tag:"general"})

  const handleClick = (e) =>{
    e.preventDefault();
} 

const handleChange = (e)=>{
    setnote({...note,[e.target.name] : e.target.value})
}

  const ref = useRef(null);

  const updateNote = (note) => {
    ref.current.click();
  };


  return (
    <>
      <AddNote />

      {/* Updating Note */}


      <button ref={ref} type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
      </button>


      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">Edit Notes</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
            <form className="my-3">
          <div className="mb-3">
            <label htmlFor="etitle" className="form-label" >
              Title
            </label>
            <input
              type="text"
              className="form-control"
              id="etitle"
              name="etitle"
              aria-describedby="emailHelp"
              onChange={handleChange}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="edescription" className="form-label">
              Description
            </label>
            <input
              type="text"
              className="form-control"
              id="edescription"
              name="edescription"
              onChange={handleChange}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="etag" className="form-label">
              Tag
            </label>
            <input
              type="text"
              className="form-control"
              id="etag"
              name="etag"
              onChange={handleChange}
            />
          </div>
        </form>

            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>




      <div className="row my-3">
        <h2>Your Notes</h2>
        {notes.map((note, i) => {
          return <NoteItem key={i} updateNote={updateNote} note={note} />;
        })}
      </div>
    </>
  );
};

export default Notes;
