import React, { useState } from "react";
import NotesContext from "./notesContext";

const NotesState = (props) => {
  const host = "http://localhost:5500";
  const notesInitial = [];

  const [notes, setnotes] = useState(notesInitial);

  //Get all notes
  const getNotes = async () => {
    const response = await fetch(`${host}/api/notes/fetchallnotes`, {
      method: "GET",
      headers: {
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjM0ZWY3N2NhOWIxZWM4OWE5MDQ1NDkzIn0sImlhdCI6MTY2NjIwNzQzOX0.UPorDoQPsWVs4YrvL2ITKG7rTE5xdnjOv02Xmty8Fis",
      }
    });
    const json = await response.json();
    console.log(response.json);
    setnotes(json);
  };

  //Add a note
  const addNote = async (title, description, tag) => {
    const response = await fetch(`${host}/api/notes/addnote`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjM0ZWY3N2NhOWIxZWM4OWE5MDQ1NDkzIn0sImlhdCI6MTY2NjIwNzQzOX0.UPorDoQPsWVs4YrvL2ITKG7rTE5xdnjOv02Xmty8Fis",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const json = response.json();

    const note = {
      _id: "6359ee09417e27064a601b8f",
      user: "634ef77ca9b1ec89a9045493",
      title: title,
      description: description,
      tag: tag,
      date: "2022-10-27T02:33:45.949Z",
      __v: 0,
    };
    setnotes(notes.concat(note));
  };
  //Delete a note
  const delNote = (id) => {
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setnotes(newNotes);
  };
  //Edit a note
  const editNote = async (id, title, description, tag) => {
    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjM0ZWY3N2NhOWIxZWM4OWE5MDQ1NDkzIn0sImlhdCI6MTY2NjIwNzQzOX0.UPorDoQPsWVs4YrvL2ITKG7rTE5xdnjOv02Xmty8Fis",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const json = response.json();

    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };

  return (
    <NotesContext.Provider
      value={{ notes, addNote, delNote, editNote, getNotes }}
    >
      {props.children}
    </NotesContext.Provider>
  );
};

export default NotesState;
